CREATE DATABASE IF NOT EXISTS pocdb;
CREATE USER IF NOT EXISTS 'pocuser'@'ip-172-31-41-46.ec2.internal' IDENTIFIED BY 'Db1@12345';
GRANT ALL PRIVILEGES ON pocuser.* TO 'pocdb'@'ip-172-31-41-46.ec2.internal';
FLUSH PRIVILEGES;

CREATE DATABASE IF NOT EXISTS database2;
CREATE USER IF NOT EXISTS 'database2'@'%' IDENTIFIED BY 'Db2@12345';
GRANT ALL PRIVILEGES ON database2.* TO 'database2'@'%';
FLUSH PRIVILEGES;
